export const code = async (inputs) => {
  const regions = (typeof inputs.regions_array === 'string' 
    ? JSON.parse(inputs.regions_array) 
    : inputs.regions_array) as any[];

  const accounts = (typeof inputs.accounts_array === 'string' 
    ? JSON.parse(inputs.accounts_array) 
    : inputs.accounts_array) as any[];

  return { regions, accounts };
};

export const code = async ({inputs}) => {
  return getHourlyPricingObject(inputs);
};

const getHourlyPricingObject = data => {
  let endpoint, glbEndpoint;

  for (const item of data || []) {
    const onDemand = item?.terms?.OnDemand;
    if (!onDemand) continue;

    for (const termKey in onDemand) {
      const term = onDemand[termKey];
      const priceDimensions = term && term.priceDimensions;
      if (!priceDimensions) continue;

      for (const dimKey in priceDimensions) {
        const dim = priceDimensions[dimKey];
        if (!dim || dim.unit !== "Hrs") continue;

        const pricePerUnitUSD = dim.pricePerUnit?.USD ? Number(dim.pricePerUnit.USD) : undefined;
        const monthlyCost = pricePerUnitUSD !== undefined
          ? Math.round(pricePerUnitUSD * 730 * 100) / 100
          : undefined;

        const entry = {
          sku: term.sku,
          description: dim.description,
          pricePerUnitUSD,
          monthlyCost,
          region: item.product?.attributes?.regionCode,
          endpointType: item.product?.attributes?.endpointType,
          usageType: item.product?.attributes?.usagetype,
          productFamily: item.product?.productFamily,
        };

        if (entry.endpointType === "PrivateLink") endpoint = entry;
        else if (entry.endpointType === "Gateway Load Balancer Endpoint") glbEndpoint = entry;
      }
    }
  }

  return { endpoint, glbEndpoint };
};

export const code = async (inputs) => {
  return bytesToHumanReadable(parseInt(inputs.bytes || 0));
};

const bytesToHumanReadable = (bytes, decimals = 2) => {
  // Validate that the input is a number.
  if (typeof bytes !== 'number' || isNaN(bytes)) {
    return 'Invalid input';
  }

  // Determine the sign for negative values.
  const sign = bytes < 0 ? '-' : '';
  bytes = Math.abs(bytes);

  // Special case: 0 bytes.
  if (bytes === 0) return '0 Bytes';

  // Define the conversion factor and available size units.
  const k = 1024;
  const dm = decimals < 0 ? 0 : decimals;
  const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];

  // Determine the appropriate unit.
  const i = Math.floor(Math.log(bytes) / Math.log(k));

  // Convert and format the number to the specified decimal places.
  return sign + parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
};
